    @SuppressWarnings("unchecked")

    private boolean isPalindrome(String Pal) {
        Pal = Pal.replaceAll("[^a-zA-Z0-9]", "").toLowerCase();
        int left = 0;
        int right = Pal.length() - 1;
        while (left < right) {
            if (Pal.charAt(left) != Pal.charAt(right)) {
                return false;
            }
            left++;
            right--;
        }
        return true;
    }

    private int[] stringStats(String word) {
        int[] stats = new int[6];
        for (char c : word.toCharArray()) {
            if (Character.isLetter(c)) {
                stats[0]++; // alphabetic characters
                if (Character.isLowerCase(c)) {
                    stats[3]++; 
                } else {
                    stats[4]++; // upper case characters
                }
            } else if (Character.isDigit(c)) {
                stats[1]++; 
            } else if (Character.isWhitespace(c)) {
                stats[5]++; 
            } else if (c >= 0 && c <= 31) {
                stats[2]++; // control characters
            }
        }
        return stats;
    }
