    private void checkButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkButtonActionPerformed
        String word = typingBox.getText();
        

        if (word.equals("")) {
            JOptionPane.showMessageDialog(this, "Oops! You have to type something",
                    "Try again :)", JOptionPane.WARNING_MESSAGE);
        } else if ( isPalindrome(word)) {
            JOptionPane.showMessageDialog(this, "This is a Palindrom!");
           
        int[] stats = stringStats(word);
        results.setText("Alphabetic characters: " + stats[0] + "\n"
                + "Numeric digits: " + stats[1] + "\n"
                + "Control characters: " + stats[2] + "\n"
                + "Lower case characters: " + stats[3] + "\n"
                + "Upper case characters: " + stats[4] + "\n"
                + "Whitespace characters: " + stats[5]);
        } else {
            JOptionPane.showMessageDialog(this, "Oops! this is not a Palindrom",
                    "Try again :)", JOptionPane.WARNING_MESSAGE);
        }
    }//GEN-LAST:event_checkButtonActionPerformed

